created-at-view.map:
function (doc) {
  var d = new Date(doc.started_at);
  emit(d.getTime(), doc.started_at);
}

function (doc) {
  var d = new Date(doc.post_date);
  emit([d.getFullYear(), d.getMonth(), d.getDate(), d.getHours(), d.getMinutes()], doc.id);
}

emotional-tone-view.map:
function (doc) {
  var tones = doc.enrichments.nlu.emotion.document.emotion;
  var top_tone = 'unknown';
  var top_tone_score = 0;
  for (var tone in tones) {
    if (tones[tone] > top_tone_score) {
      top_tone_score = tones[tone];
      top_tone = tone;
    }
  }
  emit(top_tone, 1);
}

sentiment-overtime-view.map:
function (doc) {
  var d = new Date(doc.post_date);
  emit([d.getFullYear(), d.getMonth(), d.getDate(), doc.enrichments.nlu.sentiment.document.label], 1);
}

emotional-overtime-view.map:
function (doc) {
  var d = new Date(doc.post_date);
  var tones = doc.enrichments.nlu.emotion.document.emotion;
  var top_tone = 'unknown';
  var top_tone_score = 0;
  for (var tone in tones) {
    if (tones[tone] > top_tone_score) {
      top_tone_score = tones[tone];
      top_tone = tone;
    }
  }
  emit([d.getFullYear(), d.getMonth(), d.getDate(), top_tone], 1);
}

entities-view.map:
function (doc) {
  var entities = doc.enrichments.nlu.entities;
  for (var i=0; i<entities.length; i++) {
    emit(entities[i].type, entities[i].text);
  }
}

keywords-view.map:
function (doc) {
  var keywords = doc.enrichments.nlu.keywords;
  for (var i=0; i<keywords.length; i++) {
    if (keywords[i].relevance > 0.75) {
      emit(keywords[i].text, keywords[i].relevance);
    }
  }
}
